import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Navbar from "../components/navbar_unlog";
import '../styles/pages/_settingprofil.scss';
import { useSelector } from 'react-redux';
import { differenceInYears } from 'date-fns';
import UploadImg from '../components/profil/UploadImg';

const SettingProfil = () => {

        const [name, setName] = useState("");
        const [firstname, setFirstname] = useState("");
        //const [password, setPassword] = useState("");
        const [mail, setMail] = useState("");
        //const [dob, setDob] = useState("");
        //const [gender, setGender] = useState("");
        //const [orientation, setOrientation] = useState("");
        //const [error, setError] = useState();

    const [isLoading, setIsLoading] = useState(true);
    const userData = useSelector((state) => state.userReducer)

    useEffect(() => {
        if (userData) {
            setIsLoading(false);
        }
    }, [userData]);

    if (isLoading) {
        return <div>Chargement...</div>;
    }

    //les fonctions sont ici
    // async function update_name(e)
    // {
    //     e.preventDefault();
    //     if (name.trim().length === 0)
    //     {   
    //         //gérer les erreurs ici
    //         return ;
    //     }

    //     axios({
    //         method: "post",
    //         url: `http://localhost:5000/api/user/`,
    //         withCredentials: true,
    //         data: {
    //             name,
    //         }
    //     })
    // }


    async function update_profil(e)
    {
        e.preventDefault();

        if (name.trim().length === 0 || firstname.trim().length === 0 ||
        mail.trim().length === 0 || password.trim().length === 0)
        {
            // setError({
            //     title: "Un ou plusieurs champs sont vides",
            //     message: "Veuillez remplir les champs manquants"
            // })
            console.log("c'est vide")
            return;
        }

        axios({
            method: "post",
            url: `http://localhost:5000/api/user/update_profil`,
            withCredentials: true,
            data: {
                name,
                firstname,
                //password,
                mail,
                //dob,
                //gender,
                //orientation,
            }
        })
            .then((res) => {
                if (res.status === 200) {
                    console.log('enregistrement réussi form envoyé');
                    //setFormSubmit(true);
                } else {
                    console.log("item");
                }
            })
            .catch((err) => {
                console.log(err);
                //setError(true);
                if (err.response.data.error === "password invalide" || err.response.data.error === "Le mail existe deja")
                {
                    // setError({
                    //     title: "Erreur de connection",
                    //     message: err.response.data.error
                    // })
                    console.log("Erreur de connection (mdp ou mail)")
                }
            })
    };

    //

    return (
        <div>
            <form onSubmit={update_profil} className='little_form'>
            <div>
                <h5>Prénom</h5>
                {userData && userData[0] ? <input className="little_input" required type="text" value={name} onChange={(e)=>setName(e.target.value)} id="Prénom" placeholder={userData[0].name} /> : ""}
                <input type="submit" value="Envoyer"/>
            </div>
            <div>
                <h5>Nom</h5>
                {userData && userData[0] ? <input className="" required type="text" value={firstname} onChange={(e)=>setFirstname(e.target.value)} id="Nom" placeholder={userData[0].firstname} /> : ""}
            </div>
            <div>
                <h5>Email</h5>
                {userData && userData[0] ? <input className="" required type="text" value={mail} onChange={(e)=>setMail(e.target.value)} id="Email" placeholder={userData[0].mail} /> : ""}
            </div>
            <div>
                <h5>password</h5>
            </div>
            <div>
                <h5>genre</h5>
            </div>
            <div>
                <h5>orientation</h5>
            </div>
            <div>
                <h5>bio</h5>
            </div>
            <div>
                <h5>Photo de profil</h5>
            </div>
            </form>
        </div>
    );
};

export default SettingProfil;
