import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from "../components/navbar_unlog";
import '../styles/pages/_profil.scss';
import { useSelector } from 'react-redux';
import { differenceInYears } from 'date-fns';
import axios from 'axios';

const Profil = () => {

    const [isLoading, setIsLoading] = useState(true);
    const userData = useSelector((state) => state.userReducer)
    const [user, setUser] = useState(null);
    const { id } = useParams();

    useEffect(() => {
        if (userData) {
            setIsLoading(false);
        }
    }, [userData]);

    useEffect(() => {
        const fetchUser = async () => {
            try {
                console.log(id);
                const response = await axios.get(`http://localhost:5000/api/user/${id}`);
                setUser(response.data);
                console.log(user[0].name);
            } catch (error) {
                console.error(error);
                setUser(null);
            }
        };

        fetchUser();
    }, [id]);

    if (isLoading) {
        return <div>Chargement...</div>;
    }

    if (!user) {
      return <div>404 error</div>;
    }

    return (
        <div>
            <div className="first-profil-container">
            <Navbar />
            <h1>{userData && userData[0] ? userData[0].name+ ' ' + userData[0].firstname + ', ' + differenceInYears(new Date() ,new Date(userData[0].dob)) : ''}</h1>
            <div className="top-profil-container">
                <div className="profil-picture">
                    {userData && userData[0] ? <img src={userData[0].ppicture} alt="user-pic" height="300px" width="300px"/> : '' }
                </div>
                <div className="profil-description">{userData && userData[0] && userData[0].bio !== null ? <p>{userData[0].bio}</p> : <p>Ne manquez pas l'occasion de vous pr√©senter et de rencontrer des personnes qui vous correspondent ! </p> }</div>
            </div>
            <p>name : {user.id}</p>
            <p>first name : {user.firstname}</p>
            <p>orienation : {user.orientation}</p>
            <p>genre : {user.gender}</p>
            </div>
        </div>
    );
};

export default Profil;
