import React, { useEffect, useState } from 'react';
import Navbar from "../components/navbar_unlog";
import Bullehashtag from '../components/bulle-hashtag';
import Miniphoto from "../components/miniphoto-bloc-profil"
import '../styles/pages/_profil.scss';
import { useSelector } from 'react-redux';
import { differenceInYears } from 'date-fns';
import UploadImg from '../components/profil/UploadImg';

// fonction utils a deplacer

function capitalizeFirstLetter(str) {
    return str.charAt(0).toUpperCase() + str.slice(1);
}

function calculerAge(dob) {
    const diff = Date.now() - dob.getTime();
    const age = new Date(diff);
    return Math.abs(age.getUTCFullYear() - 1970);
}

//

const Profil = () => {

    const [isLoading, setIsLoading] = useState(true);
    const userData = useSelector((state) => state.userReducer)

    useEffect(() => {
        if (userData) {
            setIsLoading(false);
        }
    }, [userData]);

    if (isLoading) {
        return <div>Chargement...</div>;
    }



    return (
        <div>
            <div className="first-profil-container">
            <Navbar />
            <h1>{userData && userData[0] ? capitalizeFirstLetter(userData[0].name) + ' ' + capitalizeFirstLetter(userData[0].firstname) + ', ' + differenceInYears(new Date() ,new Date(userData[0].dob)) : ''}</h1>
            <div className="top-profil-container">
                <div className="profil-picture">
                    {userData && userData[0] ? <img src={userData[0].ppicture} alt="user-pic" height="300px" width="300px"/> : '' }
                </div>
                <UploadImg />
                <div className="profil-description">{userData && userData[0] ? <p>{userData[0].bio}</p> : <p>Ne manquez pas l'occasion de vous présenter et de rencontrer des personnes qui vous correspondent ! Complétez votre espace de présentation maintenant.</p> }</div>
            </div>
            </div>
            <div className="second-profil-container">
                <div className="hashtah-profil-container">
                    <div className="logo-profil-hashtag"><img src="../img/hashtag-logo.svg" height="72px"/></div>
                    <div className="bulle-hashtag-container">
                        <Bullehashtag />
                        <Bullehashtag />
                        <Bullehashtag />
                        <Bullehashtag />
                        <Bullehashtag />
                        <Bullehashtag />
                        <Bullehashtag />
                        <Bullehashtag />
                    </div>
                </div>
            <div className="third-profil-container">
                <div className="description-profil-list">
                    <li className="description-profil-li">
                        <ul>Nom</ul>
                        <ul>Prenom</ul>
                        <ul>Age</ul>
                        <ul>genre</ul>
                        <ul>orientation</ul>
                        <ul>origine/yeux/taille (ect.)</ul>
                    </li>
                </div>
                <div className="container-profil-4photo">
                    <div className="bloc1-photo4">
                        <Miniphoto />
                        <Miniphoto />
                    </div>
                    <div className="bloc2-photo4">
                    <Miniphoto />
                    <Miniphoto />
                    </div>
                </div>
            </div>
            </div>
        </div>
    );
};

export default Profil;
