import React, { useState } from 'react';
import SignInForm from "../components/SignInform";
import SignUpForm from "../components/SignUpform";
import Navbar from "../components/navbar_unlog";
import '../styles/pages/connection.scss';


const Connection = () => {
    const [signUpModal, setSignUpModal] = useState(true);
    const [signInModal, setSignInModal] = useState(false);

    const handleModals = (e) => {
        if (e.target.id === "register") {
        setSignInModal(false);
        setSignUpModal(true);
        } else if (e.target.id === "login") {
            setSignUpModal(false);
            setSignInModal(true);
        }
    }
    return (
        <body className="body-connection">
        <div>
            <Navbar />
            <div className="connection-form">
                <div className="form-container">
                    <ul>
                        <li onClick={handleModals} id="register">S'inscrire</li>
                        <li onClick={handleModals} id="login">Se connecter</li>
                    </ul>
                    {signUpModal && <SignUpForm />}
                    {signInModal && <SignInForm />}
                </div>
            </div>
        </div>
        </body>
    );
};

export default Connection;
