import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { uploadPicture } from '../../actions/user.actions';
import '../../styles/components/_UploadImg.scss';

const UploadImg = () => {

    const [file, setFile] = useState();
    const dispatch = useDispatch();
    const userData = useSelector((state) => state.userReducer);

    const handlePicture = (e) => {
        e.preventDefault();
        const data = new FormData();
        data.append("name", userData[0].name);
        data.append("uid", userData[0].uid);
        data.append("file", file);

        dispatch(uploadPicture(data, userData.uid));
    }

    return (
        <form action="" onSubmit={handlePicture} className="upload-pic">
            <label htmlFor="file">Changer d'image</label>
            <input type="file" id="file" name="file" accept=".jpg, .jpeg, .png"
            onChange={(e) => setFile(e.target.files[0])}/>
            <br/>
            <input className="input-upload-img" type="submit" value="Envoyer" />
        </form>
    );
};

export default UploadImg;
