import React from 'react';

function Usersdisplay(props) {
    const { users } = props;

    // diviser les utilisateurs en groupes de 8
    const groupes = [];
    for (let i = 0; i < users.length; i += 8) {
      groupes.push(users.slice(i, i + 8));
    }

    return (
      <div>
        <h1>Liste des utilisateurs</h1>
        <div className="8group_display" style={{ display: 'block', flexWrap: 'wrap', justifyContent: 'center'}}>
          {groupes.map((groupe, index) => (
            <div key={index} style={{ display: 'flex', margin: '5px', border: "2px solid black" }}>
              {groupe.map((user) => (
                <div key={user.id}>
                  <img src={user.ppicture} alt={user.name} width="100px" height="100px" />
                  <p>{user.name} {user.firstname}</p>
                  <p>{user.dob}</p>
                  <p>{user.orientation}</p>
                </div>
              ))}
            </div>
          ))}
        </div>
      </div>
    );
  };

export default Usersdisplay;
